import React, {useState} from 'react';
function App(props) {
  const [data,setData] = useState(null)
  function getData(props){
    setData(props.target.value)
  }
  return (
    <div className="App">
      <h1>{data}</h1>
      <input type="text" onChange={getData} />
      <button on></button>
      {/* {props.message.length > 10 ? 'Too long' : props.message} */}
    </div>
  );
}

export default App;
